mapper.select:
SELECT i.*, i.flatformId as fid, i.categoryLevel1 as ca1,i.categoryLevel2 as ca2 ,i.categoryLevel3 as ca3, i.status as st1,i.versionId as ve1, d1.valueName as platform, c1.categoryName as one,c2.categoryName as tow,c3.categoryName as three, d2.valueName as state, i.downloads, v.versionNo as versions FROM app_info as i LEFT JOIN data_dictionary as d1 ON d1.valueId = i.flatformId LEFT JOIN app_category as c1 ON c1.id = i.categoryLevel1 LEFT JOIN app_category as c2 ON c2.id = i.categoryLevel2 LEFT JOIN app_category as c3 ON c3.id = i.categoryLevel3 LEFT JOIN data_dictionary as d2 ON d2.valueId = i.status LEFT JOIN app_version as v ON v.id = i.versionId
and i.status = 1 AND d1.typeCode = 'APP_FLATFORM' AND d2.typeCode = 'APP_STATUS'
and i.softwareName like CONCAT('%',#{softwareName},'%')
and status = #{state}
and categoryLevel1 = #{one}
and categoryLevel2 = #{tow}
and categoryLevel3 = #{three}
and flatformId = #{platform}
ORDER BY i.creationDate DESC limit #{pageBegin} , #{pageSize}

SELECT count(*) FROM app_info as i
LEFT JOIN data_dictionary as d1 ON d1.valueId = i.flatformId
LEFT JOIN app_category as c1 ON c1.id = i.categoryLevel1
LEFT JOIN app_category as c2 ON c2.id = i.categoryLevel2
LEFT JOIN app_category as c3 ON c3.id = i.categoryLevel3
LEFT JOIN data_dictionary as d2 ON d2.valueId = i.status
LEFT JOIN app_version as v ON v.id = i.versionId

  and i.status = 1 AND d1.typeCode = 'APP_FLATFORM' AND d2.typeCode = 'APP_STATUS'

    and i.softwareName like CONCAT('%',#{softwareName},'%')


    and status = #{state}


    and categoryLevel1 = #{one}


    and categoryLevel2 = #{tow}


    and categoryLevel3 = #{three}


    and flatformId = #{platform}
